if (!API_KEY) {
  throw new Error("set REACT_APP_GEMINI_API_KEY in .env");
}

const apiOptions: LiveClientOptions = {
  apiKey: API_KEY,
};

function App() {
  return (
    <LiveAPIProvider options={apiOptions}>
      <ChatLayout />
    </LiveAPIProvider>
  );
}

export default App;

